settings.custom_liquid:
{% for field in shop.metafields.HomeAprende.TituloContenido %}
	{% if forloop.first %}
    {% assign content-title            = shop.metafields.HomeAprende.TituloContenido %}
    {% assign content-desc             = shop.metafields.HomeAprende.DescripcionContenido %}
    {% assign content-image            = shop.metafields.HomeAprende.ImagenContenido %}
    {% assign count                    = forloop.length | minus:1 %}

    {% assign section-title            = shop.metafields.HomeAprende.TituloSeccion %}
    {% assign show-title               = shop.metafields.HomeAprende.MostrarTitulo %}
    {% assign section-desc             = shop.metafields.HomeAprende.DescripcionSeccion %}
    {% assign section-frase            = shop.metafields.HomeAprende.FraseSeccion %}

    {% if content-title or content-desc or content-image %}
      <div class="bg-container bg-white px-2 py-4">
        <div class="col mx-auto">
          {% if section-title and show-title == 1 %} <h2 class="h4 text-center text-uppercase">{{ section-title | strip }}</h2> {% endif %}
          {% if section-desc %}<p class="mt-1 text-center font-weight-bold">{{ section-desc | strip }}</p>{% endif %}
          {% if section-frase %}<p class="mt-1 text-center cite">{{ section-frase | strip }}</p>{% endif %}
          <div class="row d-flex justify-content-center{% if count > 2 %} slick-carousel-{{ section-title | handleize }}{% endif %} pt-2">
            <!-- Start Slide -->
            {% for i in (0..count) %} 
              {% assign m = content-image[i] %}
              {% assign n = content-title[i] | strip %}
              {% assign q = content-desc[i] | strip %}
              <div class="col-sm-{{ count | plus:1 }} col-lg item">
                <div class="text-center h-100 pt-2 pb-2">
                  {% if m != blank %}
                    <img class="mb-1 lazyautosizes ls-is-cached lazyloaded" data-src="{{ m }}" alt="{{ n }}" src="{{ m }}" data-sizes="auto" sizes="370px" />
                  {% endif %}
                  {% if n != blank %}
                    <p class="text-uppercase font-weight-bold">{{ n }}</p>
                  {% endif %}
                  {% if q != blank %}
                    <p class="opacity-75">{{ q }}</p>
                  {% endif %}
                </div>
              </div>
            {% endfor %}
            <!-- End Slide -->
          </div>
        </div>
      </div>

      {% if count > 2 %}
        <script type="text/javascript">
        $(document).ready(function(){
            $('.slick-carousel-{{ section-title | handleize }}').slick({
                dots: false,
                arrows: true,
                nextArrow: '<button type="button" aria-label="Next" class="slick-next slick-arrow" style="display: block;"><i class="fa fa-angle-right"></i></button>',
                prevArrow: '<button type="button" aria-label="Previous" class="slick-prev slick-arrow" style="display: block;"><i class="fa fa-angle-left"></i></button>',
                lazyload: 'ondemand',
                autoplay: true,
                autoplaySpeed: 4000,
                slidesToShow:  {{ count | plus:1 }},
                slidesToScroll: 1,
                responsive: [
                  {
                    breakpoint: 1600,
                    settings: {
                      slidesToShow: 3,
                      slidesToScroll: 1,
                      infinite: true,
                      dots: true
                    }
                  },
                  {
                    breakpoint: 992,
                    settings: {
                      slidesToShow: 3,
                      slidesToScroll: 1,
                      infinite: true,
                      dots: true
                    }
                  },
                  {
                    breakpoint: 768,
                    settings: {
                      slidesToShow: 2,
                      slidesToScroll: 1
                    }
                  },
                  {
                    breakpoint: 576,
                    settings: {
                      slidesToShow: 1,
                      slidesToScroll: 1
                    }
                  }
                ]
            });
          });
        </script>
      {% endif %}
    {% endif %}
  {% endif %}
{% endfor %}

