{%- liquid
  for field in shop.metafields.HomeOpiniones.TituloContenido
    if forloop.first
      assign contentTitle            = shop.metafields.HomeOpiniones.TituloContenido
      assign contentSubTitle         = shop.metafields.HomeOpiniones.SubtituloContenido
      assign contentDescription      = shop.metafields.HomeOpiniones.DescripcionContenido
      assign contentImageDesktop     = shop.metafields.HomeOpiniones.ImagenContenidoDesktop
      assign contentImageMobile      = shop.metafields.HomeOpiniones.ImagenContenidoMobile
      assign count                   = forloop.length | minus: 1

      assign sectionTitle            = shop.metafields.HomeOpiniones.TituloSeccion
      assign showTitle               = shop.metafields.HomeOpiniones.MostrarTitulo
      assign sectionDescription      = shop.metafields.HomeOpiniones.DescripcionSeccion

      assign slider-images           = sectionTitle | handleize | prepend: "slider-images-"
      assign slider-texts            = sectionTitle | handleize | prepend: "slider-texts-"
    endif
  endfor   
-%}

{% if contentTitle and contentDescription and contentImageDesktop %}
  <style>
    .slider-review .slick-slide {
      height: auto !important;
    }
    .slider-review .slick-track {
      display: flex;
      flex-direction: row;
      flex-wrap: nowrap;
      align-items: stretch;
    }
    .slick-list, .slick-track {
      height: 100% !important;
    }
    .slider-reviews-pics .slick-arrow {
      top: -20px;
    }
    .slider-reviews-pics .slick-prev {
      left: 0 !important;
    }    
    .slider-reviews-pics .slick-next {
      right: 0 !important;
    }
    .slider-triangle {
      position: absolute;
      top: 1px;
      left: -1px;
      width: 8rem;
      height: 100%;
      fill: #ffffff;
      z-index: 1;
      stroke: #ffffff;
    }
    .slider-review .slick-arrow:not(.slick-disabled):hover {
    color: inherit;
  }
  .slider-review .slick-arrow .fa:hover {
    opacity: 1 !important;
  }
  .image-in {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: 4px center;
  }
  .image-wrapper {
    flex: 1 1 auto;
    padding-bottom: 123%;
  }
.slider-reviews-texts, .slider-reviews-pics, .slider-triangle {
  display: none;
}
.slider-texts {
  padding-top: 3rem;
  padding-left: 2rem;
}

.slider-reviews-texts .slick-dots {
  padding-top: 20px;
}

@media ( max-width: 767px ) {
  .slider-triangle {
    top: auto;    
    bottom: -1px;
    width: 100.2%;
    height: 8rem;
    -webkit-transform: scaleX(-1);
    transform: scaleX(-1);
  }
  .image-in {
    object-position: center center;
  }
  .slider-texts {
    padding-top: 1.5rem;
  }  
}  

  </style>
  <div class="bg-container bg-gray py-4">
    <div class="col mx-auto">
      {% if sectionTitle and showTitle == 1 %} <h2 class="h4 text-center text-uppercase">{{ sectionTitle | strip }}</h2> {% endif %}
      {% if sectionDescription %}<p class="mt-1 text-center font-weight-bold">{{ sectionDescription | strip }}</p>{% endif %}
      <div class="row d-flex justify-content-center pt-3">
        <div class="col mx-auto">
          <div class="row">
            <!-- Start Slide 1 texts -->
            <div class="col-12 col-md-4 col-lg-4 col-xl-3 bg-white">              
              <div id="{{ slider-texts }}" class="slider-reviews-texts slider-review h-100">                
                {% for i in (0..count) %} 
                  <div class="">
                    <div class="d-flex justify-content-between flex-column h-100 slider-texts">
                      <p class="h5 text-uppercase font-weight-bold">{{ contentTitle[i] | strip }}</p>
                      <blockquote><p class="cite">{{ contentDescription[i] | strip }}</p></blockquote>
                      <p class="text-muted mb-2">{{ contentSubTitle[i] | strip }}</p>
                    </div>
                  </div>
                {% endfor %}
              </div>
            </div>
            <!-- End Slide 1 -->
            <!-- Start Slide 2 images -->
            <div class="col-12 col-md-8 col-lg-8 col-xl-9 my-order-sm-first bg-white no-gutters h-md-40">
              <svg viewBox="-1 0 160 500" preserveAspectRatio="none" class="slider-triangle"><polygon points="-1,-15 -1,500 160,500"></polygon></svg>
              <div id="{{ slider-images }}" class="slider-reviews-pics slider-review h-100">                
                {% for i in (0..count) %} 
                  <div class="col d-flex flex-column no-gutters" style="padding: 0 .3rem">
                    <div class="image-wrapper bg-white">
                        <picture>
                            <source 
                              media="(min-width: 68em)" 
                              srcset="{{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=100 100w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=116 116w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=134 134w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=156 156w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=182 182w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=210 210w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=244 244w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=282 282w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=328 328w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=380 380w, 
                                      {{ contentImageDesktop[i] }}?rect=0,0,410,505&amp;q=80&amp;auto=format&amp;w=410 410w" 
                                      sizes="(min-width: 75em) 25.13rem, (min-width: 64em) calc((100vw - 22.50rem) / 2), (min-width: 48em) calc(100vw - 22.50rem), 100vw"
                                    >
                                      <img id="img-{{ contentTitle[i] | handleize }}" 
                                        src="{{ contentImageMobile[i] }}?auto=compress,format&amp;rect=0,0,375,300&amp;w=375&amp;h=300" 
                                        sizes="(min-width: 75em) 25.13rem, (min-width: 64em) calc((100vw - 22.50rem) / 2), (min-width: 48em) calc(100vw - 22.50rem), 100vw" 
                                        class="image-in" 
                                        srcset="{{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=100 100w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=116 116w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=134 134w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=156 156w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=182 182w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=210 210w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=244 244w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=282 282w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=328 328w, 
                                                {{ contentImageMobile[i] }}?rect=0,0,375,300&amp;q=80&amp;auto=format&amp;w=375 375w" 
                                                alt="{{ contentTitle[i] | escape }}" 
                                                loading="lazy"
                                              >
                          </picture>
                    </div>
                  </div>
                {% endfor %}
              </div>
            </div>
            <!-- End Slide 2 -->
          </div>
        </div>
      </div>
    </div>
  </div>

  <script>
    $(document).ready(function(){  
    $('#{{ slider-images }}').slick({
      slidesToShow: 3,
      slidesToScroll: 1,
      arrows: true,
      nextArrow: '<button type="button" aria-label="Next" class="slick-next slick-arrow" style="display: block;"><i class="fa fa-long-arrow-right"></i></button>',
      prevArrow: '<button type="button" aria-label="Previous" class="slick-prev slick-arrow" style="display: block;"><i class="fa fa-long-arrow-left"></i></button>',    
      asNavFor: '#{{ slider-texts }}',
      responsive: [
        {
          breakpoint: 1600,
          settings: {
            slidesToShow: 3,
          }
        },
        {
          breakpoint: 1200,
          settings: {
            slidesToShow: 2,
          }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 1,
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,            
            arrows: false,
          }
        }
      ]
    });
    $('#{{ slider-texts }}').slick({
      slidesToShow: 1,
      slidesToScroll: 1,
      asNavFor: '#{{ slider-images }}',
      arrows: false,
      dots: false,
      responsive: [
        {
          breakpoint: 480,
          settings: {
            dots: true,
          }
        }
      ]      
    });  
    $("#{{ slider-texts }}, #{{ slider-images }}, .slider-triangle").show();
  })
  </script>
{% endif %}
