settings.custom_liquid:
{{ 'template-collection.css' | asset_url | stylesheet_tag }}
{{ 'component-loading-overlay.css' | asset_url | stylesheet_tag }}
{{ 'component-card.css' | asset_url | stylesheet_tag }}
{{ 'component-price.css' | asset_url | stylesheet_tag }}

{%- assign related_products = article.metafields.custom.related_products.value -%}
{%- if article.metafields.custom.related_products.value -%}
    {%- paginate related_products by 8 -%}
      <div class="collection page-width">
    <h2>Recommended Products</h2>

        <ul
          id="product-grid"
          class="
            grid product-grid grid--2-col-tablet-down
            grid--4-col-desktop
          "
        >
          {%- for the_product in related_products -%}
            {% assign lazy_load = false %}
            {%- if forloop.index > 2 -%}
              {%- assign lazy_load = true -%}
            {%- endif -%}
            <li class="grid__item" data-cascade>
              {% render 'card-product', 
                card_product: the_product,
                media_aspect_ratio: square,
                image_shape: default,
                show_secondary_image: true,
                show_vendor: true,
                show_rating: true,
                lazy_load: lazy_load
              %}
            </li>
          {%- endfor -%}
        </ul>
        {%- if paginate.pages > 1 -%}
          {% render 'pagination', paginate: paginate, anchor: '' %}
        {%- endif -%}
      </div>
    {%- endpaginate -%}

{%- endif -%}
