settings.custom_liquid:
{%style%}
	.collection-cover {
	  min-height: 500px;
		padding-top: 80px;
	  padding-bottom: 80px;
	  color: white!important;
	}

	@media (max-width: 750px) {
	  .collection-cover {
		min-height: 200px;
	  }
	}

	.collection-cover-empty {

	  color: white!important;
	}



	.collection-cover.beer {
	background: rgb(255,178,100);
	background: linear-gradient(90deg, rgba(255,178,100,1) 0%, rgba(214,123,30,1) 65%);
	color: black!important;
	}

	.collection-cover.wine {
	color: white!important;
	background: rgb(237,99,95);
	background: linear-gradient(90deg, rgba(237,99,95,1) 0%, rgba(139,85,84,1) 100%);
	}

	.collection-cover.cider{
	color: white!important;
	background: rgb(59,198,107);
	background: linear-gradient(90deg, rgba(59,198,107,1) 0%, rgba(13,107,46,1) 100%);
	}

	.collection-cover.spirits {
	color: white!important;
	background: rgb(86,234,255);
	background: linear-gradient(90deg, rgba(86,234,255,1) 0%, rgba(145,166,255,1) 100%);
	}

	.collection-cover.cocktails {
	background: rgb(69,45,253);
	background: linear-gradient(90deg, rgba(69,45,253,1) 0%, rgba(145,40,158,1) 65%);
	color: white!important;
	}

	.collection-cover.cocktails h1 {
	  color: white!important;
	}

	.collection-cover.glutenfree {
	background: rgb(252,223,153);
	background: linear-gradient(90deg, rgba(252,223,153,1) 0%, rgba(241,231,207,1) 65%);
	color: black!important;
	}

	.collection-cover.vegan {
	background: rgb(41,139,42);
	background: linear-gradient(90deg, rgba(41,139,42,1) 0%, rgba(213,253,45,1) 100%);
	color: white!important;
	}



	.collection-cover.vegan h1 {
	  color: white!important;
	}




	.collection-cover.newaf {
	color: white!important;
	background: rgb(236,236,236);
	background: linear-gradient(90deg, rgba(236,236,236,1) 0%, rgba(255,255,255,1) 51%);
	}

	.collection-cover.mixedcases {
	background: rgb(255,178,100);
	background: linear-gradient(90deg, rgba(255,178,100,1) 0%, rgba(214,123,30,1) 65%);
	color: black!important;
	}




	.vendor-cover {

	  background-position: center center;
		background-repeat: no-repeat;
		height: auto;
		background-size: cover;
		min-height: 500px;
	  padding-top: 80px;
	  padding-bottom: 80px;
	 box-shadow:inset 0 0 0 2000px rgba(0, 0, 0, 0.4);
	display: flex;
	justify-content: middle;
	align-items: center;
	}

	@media (max-width: 750px) { 
	.vendor-cover {
	padding-top: 40px!important;
	  padding-bottom: 40px!important;
	}
	}

	.vendor-cover .more-info {
	  color: white;
	  font-weight: bold;
	  text-decoration: underline;
	}

	.vendor-cover h1 {
	  color: white!important;
	text-shadow: 3px 3px 0px rgba(0,0,0,0.41);
text-align: center;
	}

	.vendor-cover p {
	  color: white!important;
text-align: center;
	}



	.vendor-cover .vendor-logo {
	 width: auto;
	   height: 120px;
	  border-radius: 50%;
	  max-height: 120px;
display: block;
	}

@media (max-width: 750px) { 
	.vendor-cover .vendor-logo {
	height: 80px;
	}
	}



	.countdown {
	margin-left: auto;
	margin-right: auto;
	width: 250px;
	}

	@media (max-width: 750px) { 
	.countdown {
	width: auto;
	}
	}

	.countdown {
	font-family: 'Montserrat', sans-serif;
	font-weight: 800;
	text-transform: uppercase;
	}

	.countdown .hours, .countdown .min, .countdown .sec {
	display: block;
	font-style: italic;
	color: white!important;
	}
	{%endstyle%}

	{% if collection.metafields.collection.background != blank and collection.metafields.collection.title != blank %}
	<div class="collection-cover {{ collection.metafields.collection.background }}">
	<div class="page-width">
	  <h1>
		<span class="visually-hidden">{{ 'sections.collection_template.title' | t }}: </span>
		{{ collection.metafields.collection.title }}
	  </h1>
	  <p>{{ collection.metafields.collection.description }}</p>
	</div>
	</div>
	{% if collection.metafields.collection.vendor_promotion != blank %}
{{ 'countdown.js'  | asset_url | script_tag     }}
	<script type="text/javascript">
	countdown('{{ collection.metafields.collection.discount_end_date }}');
	</script>




	<div class="vendor-promotion align-middle" id="vendor-promotion"><h2>{{collection.metafields.collection.vendor_promotion}}</h2>
		<p>{% if collection.metafields.collection.discount_code != blank %} - use code: <span style="background-color: black; color: white; padding: 3px; border-radius: 3px;">{{collection.metafields.collection.discount_code}}</span>{%else%}no code needed{%endif%}</p>
		{%if collection.metafields.discount.timer == true%}
	<div class="countdown mt-3">
	  <div class="row justify-content-around">
	  <div class="col-4">
		<span id="hours" class="h2"></span>
		<span class="hours">Hrs</span>
		</div>
		<div class="col-4">
		  <span id="minutes" class="h2"></span>
		  <span class="min">Min</span>
		</div>
		<div class="col-4">
		  <span id="seconds" class="h2"></span>
		  <span class="sec">Sec</span>
		</div>
	  </div>



	</div>
		{%endif%}
	</div>
	{%endif%}

	{% elsif collection.metafields.collection.vendor_cover_photo != blank or collection.metafields.collection.vendor_cover_colour != blank and collection.metafields.collection.title != blank %}
	<div class="vendor-cover d-flex h-100 flex-column justify-content-center align-items-center align-middle"

		  {% if collection.metafields.collection.vendor_cover_photo != blank %} style="background-image: url('{{ collection.metafields.collection.vendor_cover_photo | img_url: 'master' }}'); background-color: {{collection.metafields.collection.vendor_cover_colour}};" {%else%} style="background-color: {{collection.metafields.collection.vendor_cover_colour}};" {%endif%}>
		<div class="page-width">
		<div>
		 <img src="{{ collection.metafields.collection.vendor_logo_photo | img_url: 'master' }}" class="vendor-logo mx-auto">
		</div>
			  <div>
			  <h1 class="mx-auto">
		<span class="visually-hidden">{{ 'sections.collection_template.title' | t }}: </span>
		{{ collection.metafields.collection.title }}
	  </h1>


			  </div>
			  <div>
			   <p class="text-center">{{ collection.metafields.collection.description }} {% if collection.metafields.collection.vendor_full_descriptipn != blank %}<a href="#about" class="more-info">Find out more..</a>{%endif%}</p>

			  </div>

		</div>
			</div>
	{% if collection.metafields.collection.vendor_promotion != blank %}
{{ 'countdown.js'  | asset_url | script_tag     }}
	<script type="text/javascript">
	countdown('{{ collection.metafields.collection.discount_end_date }}');
	</script>




	<div class="vendor-promotion align-middle" id="vendor-promotion"><h2>{{collection.metafields.collection.vendor_promotion}}</h2>
		<p>{% if collection.metafields.collection.discount_code != blank %} - use code: <span style="background-color: black; color: white; padding: 3px; border-radius: 3px;">{{collection.metafields.collection.discount_code}}</span>{%else%}no code needed{%endif%}</p>
		{%if collection.metafields.discount.timer == true%}
	<div class="countdown mt-3">
	  <div class="row justify-content-around">
	  <div class="col-4">
		<span id="hours" class="h2"></span>
		<span class="hours">Hrs</span>
		</div>
		<div class="col-4">
		  <span id="minutes" class="h2"></span>
		  <span class="min">Min</span>
		</div>
		<div class="col-4">
		  <span id="seconds" class="h2"></span>
		  <span class="sec">Sec</span>
		</div>
	  </div>



	</div>
		{%endif%}
	</div>
	{%endif%}
	{%else%}
	<div class="collection-cover-empty">
	<div class="page-width">
	<h1>
	<span class="visually-hidden">{{ 'sections.collection_template.title' | t }}: </span>
	{{- collection.title | escape -}}
	</h1>
	<p>{{ collection.description }}</p>
	</div>
	</div>
	{% if collection.metafields.collection.vendor_promotion != blank %}
{{ 'countdown.js'  | asset_url | script_tag     }}
	<script type="text/javascript">
	countdown('{{ collection.metafields.collection.discount_end_date }}');
	</script>




	<div class="vendor-promotion align-middle" id="vendor-promotion"><h2>{{collection.metafields.collection.vendor_promotion}}</h2>
		<p>{% if collection.metafields.collection.discount_code != blank %} - use code: <span style="background-color: black; color: white; padding: 3px; border-radius: 3px;">{{collection.metafields.collection.discount_code}}</span>{%else%}no code needed{%endif%}</p>
		{%if collection.metafields.discount.timer == true%}
	<div class="countdown mt-3">
	  <div class="row justify-content-around">
	  <div class="col-4">
		<span id="hours" class="h2"></span>
		<span class="hours">Hrs</span>
		</div>
		<div class="col-4">
		  <span id="minutes" class="h2"></span>
		  <span class="min">Min</span>
		</div>
		<div class="col-4">
		  <span id="seconds" class="h2"></span>
		  <span class="sec">Sec</span>
		</div>
	  </div>



	</div>
		{%endif%}
	</div>
	{%endif%}
	{%endif%}

{{ '//cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css' | stylesheet_tag }}
{{ 'bootstrap-grid.css' | asset_url | stylesheet_tag }}
{%style%}
.vendor-about .vendor-about-image {
 width: 100%!important;
   height: auto;
object-fit: cover;
height: 300px;

}

.vendor-about p:first-child {
margin-top: 0;
margin-bottom: 0;
}

.vendor-promotion {
  padding-top: 10px;
  padding-bottom: 10px;
  text-align: center;
  background-color: #ff5252;
  color: white;
  text-transform: uppercase;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
}

.vendor-socials .icon {
  color: black;
  text-decoration: none;

}

.vendor-socials .instagram:before {
   font-family: 'Font Awesome 6 Brands';
  font-weight: 400;
   content: "\f16d\00a0";
}

.vendor-socials .twitter:before {
   font-family: 'Font Awesome 6 Brands';
  font-weight: 400;
   content: "\f099\00a0";
}

.vendor-socials .website:before {
   font-family: 'Font Awesome 6 Free';
  font-weight: 900;
   content: "\f0c1\00a0";
}

@media (max-width: 750px) {
  .vendor-cover {
    min-height: 200px;
  }
}
{%endstyle%}
{% if collection.metafields.collection.vendor_full_descriptipn != blank %}
<div id="about" class="page-width vendor-about">
<h2>About {{ collection.title }}</h2>
<div class="row">
<div class="col-md-4">
<img src="{{ collection.metafields.collection.vendor_about_image | img_url: 'master' }}" class="vendor-about-image mb-4">
</div>
<div class="col-md-8">
{{ collection.metafields.collection.vendor_full_descriptipn }}
<div class="vendor-socials">
<p>
{% if collection.metafields.collection.vendor_instagram %}
<a href="{{ collection.metafields.collection.vendor_instagram }}" class="icon instagram" rel="nofollow"> </a>
{%endif%}
{% if collection.metafields.collection.vendor_twitter %}
<a href="{{ collection.metafields.collection.vendor_twitter}}" class="icon twitter" rel="nofollow"> </a>
{%endif%}
{% if collection.metafields.collection.vendor_website%}
<a href="{{ collection.metafields.collection.vendor_website }}" class="icon website" rel="nofollow"><strong>{{collection.metafields.collection.vendor_website  | remove: "https://" | remove: "http://" | remove: "www." | remove: "/" }}</strong></a>
{%endif%}
</p>
</div>
</div>
</div>
</div>
{%endif%}
