settings.custom_liquid:
{%style%}
.collection-cover {
  min-height: 500px;
	padding-top: 80px;
  padding-bottom: 80px;
  color: white!important;
}

@media (max-width: 750px) {
  .collection-cover {
    min-height: 200px;
  }
}

.collection-cover.beer {
background: rgb(255,178,100);
background: linear-gradient(90deg, rgba(255,178,100,1) 0%, rgba(214,123,30,1) 65%);
color: black!important;
}

.collection-cover.wine {
color: white!important;
background: rgb(237,99,95);
background: linear-gradient(90deg, rgba(237,99,95,1) 0%, rgba(139,85,84,1) 100%);
}

.collection-cover.cider{
color: white!important;
background: rgb(59,198,107);
background: linear-gradient(90deg, rgba(59,198,107,1) 0%, rgba(13,107,46,1) 100%);
}

.collection-cover.spirits {
color: white!important;
background: rgb(86,234,255);
background: linear-gradient(90deg, rgba(86,234,255,1) 0%, rgba(145,166,255,1) 100%);
}

.collection-cover.cocktails {
background: rgb(69,45,253);
background: linear-gradient(90deg, rgba(69,45,253,1) 0%, rgba(145,40,158,1) 65%);
color: white!important;
}

.collection-cover.cocktails h1 {
  color: white!important;
}

.collection-cover.glutenfree {
background: rgb(252,223,153);
background: linear-gradient(90deg, rgba(252,223,153,1) 0%, rgba(241,231,207,1) 65%);
color: black!important;
}

.collection-cover.vegan {
background: rgb(41,139,42);
background: linear-gradient(90deg, rgba(41,139,42,1) 0%, rgba(213,253,45,1) 100%);
color: white!important;
}



