{{ '//cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css' | stylesheet_tag }}
{{ 'bootstrap-grid.css' | asset_url | stylesheet_tag }}
{%style%}
.vendor-about .vendor-about-image {
 width: 100%!important;
   height: auto;
}

.vendor-about p:first-child {
margin-top: 0;
margin-bottom: 0;
}

.vendor-promotion {
  padding-top: 10px;
  padding-bottom: 10px;
  text-align: center;
  background-color: #ff5252;
  color: white;
  text-transform: uppercase;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
}

.vendor-socials .icon {
  color: black;
  text-decoration: none;

}

.vendor-socials .instagram:before {
   font-family: 'Font Awesome 6 Brands';
  font-weight: 400;
   content: "\f16d\00a0";
}

.vendor-socials .twitter:before {
   font-family: 'Font Awesome 6 Brands';
  font-weight: 400;
   content: "\f099\00a0";
}

.vendor-socials .website:before {
   font-family: 'Font Awesome 6 Free';
  font-weight: 900;
   content: "\f0c1\00a0";
}

@media (max-width: 750px) {
  .vendor-cover {
    min-height: 200px;
  }
}
{%endstyle%}
{% if collection.metafields.collection.vendor_full_descriptipn != blank %}
<div id="about" class="page-width vendor-about">
<h2>About {{ collection.title }}</h2>
<div class="row">
<div class="col-md-4">
<img src="{{ collection.metafields.collection.vendor_about_image | img_url: 'master' }}" class="vendor-about-image mb-4">
</div>
<div class="col-md-8">
{{ collection.metafields.collection.vendor_full_descriptipn }}
<div class="vendor-socials">
<p>
{% if collection.metafields.collection.vendor_instagram %}
<a href="{{ collection.metafields.collection.vendor_instagram }}" class="icon instagram" rel="nofollow"> </a>
{%endif%}
{% if collection.metafields.collection.vendor_twitter %}
<a href="{{ collection.metafields.collection.vendor_twitter}}" class="icon twitter" rel="nofollow"> </a>
{%endif%}
{% if collection.metafields.collection.vendor_website%}
<a href="{{ collection.metafields.collection.vendor_website }}" class="icon website" rel="nofollow"><strong>{{collection.metafields.collection.vendor_website  | remove: "https://" | remove: "http://" | remove: "www." | remove: "/" }}</strong></a>
{%endif%}
</p>
</div>
</div>
</div>
</div>
{%endif%}
