settings.custom_liquid:
{%style%}
.product-feature-tags {
 display: flex; 
  margin: 0;
  padding: 0;
  list-style: none;
}

.product-feature-tags a {
  background-color: #f0f0f0;
  border-radius: 50px;
  padding: 0 15px;
  font-weight: bold;
  font-size: 13px;
  display: block;
  line-height: 32px;
  margin: 5px 5px 0 0;
  text-align: center;
  color: black !important;
  text-decoration: none!important;
}

.product-feature-tags .vegan {
background-color: #6ab04c;
color: white!important;
}
{%endstyle%}
{% if product.metafields.check.vegan != blank %}
<ul class="product-feature-tags">
{% if product.metafields.check.vegan == true %}
<li><a class="vegan">Vegan</a></li>
{%endif%}
{% if product.metafields.check.glutenfree == true %}
<li><a>Gluten Free</a></li>
{%endif%}
</ul>
{% endif %}

{{ '//cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css' | stylesheet_tag }}
