settings.custom_liquid:
{% capture var %}{% render 'locksmith-variables', variable: 'access_granted', scope: 'subject', subject: product %}{% endcapture %}{% if var == 'true' %}{% assign locksmith_access_granted = true %}{% else %}{% assign locksmith_access_granted = false %}{% endif %}


{% if locksmith_access_granted %}
<!-- Zip WIDGET -->
<div class="zip-product">
{%render 'zip-widget'%}
<span style="cursor:pointer;" id="shopify-zip-prod-widget" data-zm-asset="productwidget" data-zm-widget="popup" data-zm-popup-asset="termsdialog"></span>
</div>
<!-- end Zip WIDGET -->

<div class="afterpay-outer">
<div class="afterpay-inner">
</div>
</div>
{% endif %}

{% capture my_collection_handle %}
  {{ product.vendor | handleize | strip | escape }}
{% endcapture %}

{% assign my_collection_handle_stripped = my_collection_handle | strip | escape %}

{% for collection in collections %}
  {% if my_collection_handle_stripped == collection.handle %}
    {% assign brand_image = collection.image %}
    {% assign brand_description = collection.description %}
    {% assign its_a_match = true %}
  {% endif %}
{% endfor %}

{% if brand_description != blank %}

{{ 'component-image-with-text.css' | asset_url | stylesheet_tag }}

<div class="image-with-text image-with-text--no-overlap page-width isolate collapse-corners brand-info-on-product-page">
  <div class="image-with-text__grid grid grid--gapless grid--1-col grid--2-col-tablet image-with-text__grid--reverse">
    <div class="image-with-text__media-item image-with-text__media-item--medium image-with-text__media-item--top grid__item">
      <div class="image-with-text__media gradient color-background-2 global-media-settings image-with-text__media--placeholder placeholder" align="center">
        <img src="{{ brand_image | img_url: 'large' }}" style="padding-top: 6rem;"/>
      </div>
    </div>
    <div class="image-with-text__text-item grid__item">
      <div class="image-with-text__content image-with-text__content--top image-with-text__content--desktop-left image-with-text__content--mobile-left image-with-text__content--small gradient color-background-2 content-container">
        <h2 class="image-with-text__heading h1">
          {{ product.vendor }}
        </h2>
        <div class="image-with-text__text rte body"><p>{{ brand_description }} <a class="link animate-arrow" href="/collections/{{ my_collection_handle_stripped | handleize }}">Shop more from {{ product.vendor }} <span class="icon-wrap">&nbsp;{% render 'icon-arrow' %}</span></a></p></div>
      </div>
    </div>
  </div>
</div>

{% endif %}
