settings.custom_liquid:
{%style%}
.product-feature-tags {
 display: flex; 
  margin: 0;
  padding: 0;
  list-style: none;
}

.product-feature-tags a {
  background-color: #f0f0f0;
  border-radius: 50px;
  padding: 0 15px;
  font-weight: bold;
  font-size: 13px;
  display: block;
  line-height: 32px;
  margin: 5px 5px 0 0;
  text-align: center;
  color: black !important;
  text-decoration: none!important;
}

.product-feature-tags .vegan {
background-color: #6ab04c;
color: white!important;
}
{%endstyle%}
{% if product.metafields.check.vegan != blank %}
<ul class="product-feature-tags">
{% if product.metafields.check.vegan == true %}
<li><a class="vegan">Vegan</a></li>
{%endif%}
{% if product.metafields.check.glutenfree == true %}
<li><a>Gluten Free</a></li>
{%endif%}
</ul>
{% endif %}

{%style%}
.tabs {
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  overflow: scroll;
  -webkit-overflow-scrolling: touch;
  height: 50px;
  margin-bottom: 20px;
  width: auto;
}

.tabs::-webkit-scrollbar {
          display: none;
        }

.tabs li a:first-child {


}

.tabs li a {

  white-space: nowrap!important;
  font-size: 18px;
  text-decoration: none;
  font-weight: bold;
 border-bottom: 6px #f5f5f5 solid;

  min-width: 50px;
 padding: 0 40px 10px;
  color: #84817a;


}

.tabs li a:first-child {


}


.tabs li a:last-child {

}

.tabs li a {

  white-space: nowrap!important;


}


.tabs li:hover a {

   color: black!important; 
}

.tabs li a:hover {

    border-bottom: 6px #FFC040 solid!important;
}

.tabscroll_activeNavi a {
 color: black!important; 
  border-bottom: 6px #FFC040 solid!important;
  white-space: nowrap;
}

.tabs a {
color: #84817a;
  text-decoration: none!important;
  white-space: nowrap;

}
{%endstyle%}
<div id="more" class="page-width">
	<ul class="tabs" data-tabscrollnavcontainer>
		<li><a href="#description">Description</a></li>
		<li><a href="#tasting-notes">Tasting Notes</a></li>
		<li><a href="#ingredients">Ingredients</a></li>
		<li><a href="#nutrition">Nutrition</a></li>
		<li><a href="#reviews">Reviews</a></li>
	  </ul>
		<section id="description">
		 {{ product.description }}
		</section>

		<section id="tasting-notes">
		  <h2>Tasting Notes</h2>

	{% if product.metafields.tasting_note.light != blank or
	product.metafields.tasting_note.dark != blank or
	product.metafields.tasting_note.light != blank or
	product.metafields.tasting_note.balanced != blank or
	product.metafields.tasting_note.sweet != blank or
	product.metafields.tasting_note.sour != blank or
	product.metafields.tasting_note.bitter != blank or
	product.metafields.tasting_note.smooth != blank or
	product.metafields.tasting_note.hazy != blank or
	product.metafields.tasting_note.clean != blank or
	product.metafields.tasting_note.juicy != blank or
	product.metafields.tasting_note.floral != blank or
	product.metafields.tasting_note.earthy != blank or
	product.metafields.tasting_note.tropical != blank  or
	product.metafields.tasting_note.citrus != blank or
	product.metafields.tasting_note.pine != blank  or
	product.metafields.tasting_note.coffee != blank  or
	product.metafields.tasting_note.hoppy != blank or
	product.metafields.tasting_note.fruity != blank or
	product.metafields.tasting_note.caramel != blank   %}
		  <div class="tasting-notes-container">
	{% if product.metafields.tasting_note.light == true %}
	<div class="tasting-note-icon">
	<h3>Light</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/light.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.dark == true %}
	<div class="tasting-note-icon">
	<h3>Dark</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/dark_beer.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.balanced == true %}
	<div class="tasting-note-icon">
	<h3>Balanced</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/balanced_beer.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.sweet == true %}
	<div class="tasting-note-icon">
	<h3>Sweet</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/sweet.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.sour == true %}
	<div class="tasting-note-icon">
	<h3>Sour</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/sour.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.bitter == true %}
	<div class="tasting-note-icon">
	<h3>Bitter</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/bitter.png"/>
	</div>
	{%endif%}

	 {% if product.metafields.tasting_note.smooth == true %}
	<div class="tasting-note-icon">
	<h3>Smooth</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/smooth.png"/>
	</div>
	{%endif%}



	{% if product.metafields.tasting_note.hazy == true %}
	<div class="tasting-note-icon">
	<h3>Hazy</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/hazy.png"/>
	</div>
	{%endif%}

	 {% if product.metafields.tasting_note.clean == true %}
	<div class="tasting-note-icon">
	<h3>Clean</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/clean.png"/>
	</div>
	{%endif%}

	 {% if product.metafields.tasting_note.juicy == true %}
	<div class="tasting-note-icon">
	<h3>Juicy</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/juicy.png"/>
	</div>
	{%endif%}

	 {% if product.metafields.tasting_note.floral == true %}
	<div class="tasting-note-icon">
	<h3>Floral</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/floral.png"/>
	</div>
	{%endif%}

	 {% if product.metafields.tasting_note.earthy == true %}
	<div class="tasting-note-icon">
	<h3>Earthy</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/earthy.png"/>
	</div>
	{%endif%}


	 {% if product.metafields.tasting_note.tropical == true %}
	<div class="tasting-note-icon">
	<h3>Tropical</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/tropical.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.citrus == true %}
	<div class="tasting-note-icon">
	<h3>Citrus</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/citrus.png"/>
	</div>
	{%endif%}


	{% if product.metafields.tasting_note.pine == true %}
	<div class="tasting-note-icon">
	<h3>Pine</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/pine.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.coffee == true %}
	<div class="tasting-note-icon">
	<h3>Coffee</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/coffee.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.hoppy == true %}
	<div class="tasting-note-icon">
	<h3>Hoppy</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/hoppy.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.fruity == true %}
	<div class="tasting-note-icon">
	<h3>Fruity</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/fruity.png"/>
	</div>
	{%endif%}

	{% if product.metafields.tasting_note.caramel == true %}
	<div class="tasting-note-icon">
	<h3>Caramel</h3>
	<img 
	src="https://cdn.shopify.com/s/files/1/2071/7563/files/caramel.png"/>
	</div>
	{%endif%}


	</div>
	{%else%}
	<p>No tasting notes available.</p>
	{%endif%}
		</section>

	<section id="ingredients">
		  <h2>Ingredients</h2>
		{%if product.metafields.product.ingredients_tab != blank%}
		  {{ product.metafields.product.ingredients_tab }}
		{%else%}
		<p>No ingredients to display</p>
		{%endif%}
		</section>

	<section id="nutrition">
		  <h2>Nutrition</h2>
		{% if product.metafields.nutrient.protein != blank or
		product.metafields.nutrient.sugar != blank or
		product.metafields.nutrient.kcal != blank
		%}
		  <div class="tab-inner-content">

	<div class="nutrition-highlight-container">
		<div class="nutrition-highlight">
	  <div class="nutrition-amount">{{product.metafields.nutrient.protein | divided_by: 100 | times: product.metafields.drink.size | round }}g</div>
		<div class="nutrition-nutrient">Protein</div>
		<div class="nutrition-serving">Per {{product.metafields.drink.size}}ml {{product.metafields.product.packaging_type}}</div>
			</div>
		<div class="nutrition-highlight">
	  <div class="nutrition-amount">{{product.metafields.nutrient.sugar | divided_by: 100 | times: product.metafields.drink.size }}g</div>
		<div class="nutrition-nutrient">Sugar</div>
		<div class="nutrition-serving">Per {{product.metafields.drink.size}}ml {{product.metafields.product.packaging_type}}</div>
			</div>
		<div class="nutrition-highlight">
	  <div class="nutrition-amount">{{product.metafields.nutrient.kcal | divided_by: 100 | times: product.metafields.drink.size | round }}</div>
		<div class="nutrition-nutrient">Calories</div>
		<div class="nutrition-serving">Per {{product.metafields.drink.size}}ml {{product.metafields.product.packaging_type}}</div>
			</div>
	</div>

	</div>
			{%endif%}
	<div class="tab-inner-content">
	<table class="table-nutrition">
		<thead>
		<tr>
		<th>Nutrition</th>
		<th>per 100ml</th>
		<th>per {{product.metafields.drink.size}}ml</th>
		</tr>
		</thead>
		<tbody>
		<tr>
		<td>Energy kJ/kcal</td>
		<td>{{product.metafields.nutrient.energykj }}/{{product.metafields.nutrient.kcal }}</td>
		<td>{{product.metafields.nutrient.energykj | divided_by: 100 | times: product.metafields.drink.size }}/{{product.metafields.nutrient.kcal | divided_by: 100 | times: product.metafields.drink.size }}</td>
		</tr>
		<tr>
		<td>Fat<br><i>of which saturates</i></td>
		<td>{{product.metafields.nutrient.fat}}g<br><i>{{product.metafields.nutrient.saturates }}g</i></td>
		<td>{{product.metafields.nutrient.fat | divided_by: 100 | times: product.metafields.drink.size }}g<br>
		  <i>{{product.metafields.nutrient.saturates | divided_by: 100 | times: product.metafields.drink.size }}g</i></td>
		</tr>
		<tr>
		<td>Carbohydrates<br><i>of which sugars</i></td>
		<td>{{product.metafields.nutrient.carbs }}g<br><i>{{product.metafields.nutrient.sugar }}g</i></td>
		<td>{{product.metafields.nutrient.carbs | divided_by: 100 | times: product.metafields.drink.size }}g<br>
		  <i>{{product.metafields.nutrient.sugar | divided_by: 100 | times: product.metafields.drink.size }}g</i></td>
		</tr>
		<tr>
		<td>Fibre</td>
		<td>{% if product.metafields.nutrient.fibre == blank%}Nill{%else%}{{product.metafields.nutrient.fibre }}g{%endif%}</td>
		<td>{% if product.metafields.nutrient.fibre == blank%}{{product.metafields.nutrient.fibre | divided_by: 100 | times: product.metafields.drink.size  }}g{%endif%}</td>
		</tr>
		<tr>
		<td>Protein</td>
		<td>{{product.metafields.nutrient.protein }}g</td>
		<td>{{product.metafields.nutrient.protein | divided_by: 100 | times: product.metafields.drink.size }}g</td>
		</tr>
		<tr>
		<td>Salt</td>
		<td>{{product.metafields.nutrient.salt }}g</td>
		<td>{{product.metafields.nutrient.salt | divided_by: 100 | times: product.metafields.drink.size  }}g</td>
		</tr>
		</tbody>
		</table>
	</div>
		</section>
		<section id="reviews">
		  <div id="shopify-product-reviews" data-id="{{product.id}}">{{ product.metafields.spr.reviews }}</div>
		</section>
	</div>
