settings.custom_liquid:
{%- style -%}

.xcp-navbar {
    display: flex;
    padding: 14px 20px;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto
}

@media (min-width: 959px) {
    .xcp-navbar {
        padding:16px 30px
    }
}

.xcp-navbar__container {
    background-color: #232728
}

.xcp-navbar__container a {
    font-style: normal;
    color: #d8d8d8
}

.xcp-navbar__container a:hover {
    color: #ffb256;
    cursor: pointer
}

.xcp-navbar__container a:hover,.xcp-navbar__container a:visited,.xcp-navbar__container a:focus,.xcp-navbar__container a:active {
    text-decoration: none
}

.xcp-navbar__container a.xcp-navbar__order-now {
    color: #333636
}

.xcp-navbar__container a.current-page {
    color: #666
}

.xcp-navbar__container--fixed {
    position: fixed;
    top: 0;
    z-index: 1;
    width: 100%;
    box-shadow: unset
}

.xcp-navbar__mobile--expanded {
    background-color: #333636
}

@media (min-width: 959px) {
    .xcp-navbar__mobile {
        display:none
    }
}

.xcp-navbar__desktop {
    display: none
}

@media (min-width: 959px) {
    .xcp-navbar__desktop {
        display:flex
    }
}

@media (min-width: 959px) {
    .xcp-navbar__logo {
        display:none
    }
}

.xcp-navbar__logo--desktop {
    display: none
}

@media (min-width: 959px) {
    .xcp-navbar__logo--desktop {
        display:flex;
        margin-left: 10px
    }

    .xcp-navbar__logo--desktop img {
        height: 50px
    }
}

.xcp-navbar__xcp-logo {
    font-size: 22px
}

.xcp-navbar__xcp-logo--x-carve {
    font-weight: bold
}

.xcp-navbar__xcp-logo a {
    color: #fff
}

@media only screen and (min-width: 768px) {
    .xcp-navbar__xcp-logo {
        font-size:30px
    }
}

.xcp-navbar__order-now {
    font-size: 12px;
    padding: 2px 10px
}

@media (min-width: 415px) {
    .xcp-navbar__order-now {
        padding:6px 16px
    }
}

@media (min-width: 959px) {
    .xcp-navbar__order-now {
        padding:4px 16px
    }
}

.xcp-navbar__schedule-demo {
    font-size: 12px;
    padding: 2px 10px;
    margin-right: 16px;
    background-color: #eee
}

@media (min-width: 415px) {
    .xcp-navbar__schedule-demo {
        padding:6px 16px
    }
}

@media (min-width: 959px) {
    .xcp-navbar__schedule-demo {
        padding:4px 16px
    }
}

.xcp-navbar__menu {
    z-index: 100;
    list-style-type: none;
    margin: 0;
    margin-top: -1px;
    width: 100%;
    display: none;
    flex-direction: column;
    align-items: flex-start;
    position: absolute;
    background-color: #333636;
    padding: 10px 50px;
    box-sizing: border-box
}

.xcp-navbar__menu-button {
    color: #d8d8d8;
    padding: 12px
}

@media (min-width: 959px) {
    .xcp-navbar__menu-button {
        display:none
    }
}

.xcp-navbar__menu-button:hover {
    cursor: pointer
}

.xcp-navbar__menu-item {
    width: 100%;
    border-bottom: 1px solid #d8d8d8;
    font-weight: 100;
    font-size: 14px;
    color: #d8d8d8;
    text-transform: uppercase
}

.xcp-navbar__menu-item:last-child {
    border-bottom: 0
}

.xcp-navbar__menu-item p {
    margin-top: 20px;
    margin-bottom: 16px
}

.xcp-navbar__menu--desktop {
    display: none
}

@media (min-width: 959px) {
    .xcp-navbar__menu--desktop {
        list-style-type:none;
        margin: 0;
        display: flex;
        align-items: center
    }
}

.xcp-navbar__menu-item--desktop {
    display: none
}

@media (min-width: 959px) {
    .xcp-navbar__menu-item--desktop {
        font-size:13px;
        font-weight: 100;
        display: flex;
        justify-content: flex-start;
        color: #eee;
        text-transform: uppercase;
        padding-right: 50px
    }
}

.xcp-navbar__show {
    display: flex
}
{%- endstyle -%}


<div class="xcp-navbar__container" data-target="xcp-navbar">
    <nav class="xcp-navbar xcp-navbar__desktop">
  <span class="xcp-navbar__xcp-logo">
    <a href="/technologies/x-carve-pro">
      <span class="xcp-navbar__xcp-logo--x-carve">X-CARVE </span>Pro
