settings.custom_liquid:
{% style %}

@media only screen and (max-width: 960px) {
    .affirm-as-low-as {
        margin-top:-4px
    }
}

.affirm-as-low-as .affirm-modal-trigger {
    color: #eee;
    text-decoration: underline
}


{% endstyle %}

<div class="affirm-as-low-as" data-learnmore-show="true" data-page-type="product" data-affirm-color="white" data-amount="{{ product.selected_or_first_available_variant.price  }}">Or for a low monthly price with Affirm.</div>

                <div class="kit-select__affirm-explanation-message">
                  Financing payments begin after the unit ships.
                </div>

{% style %}
.xcp-size-select {
  display: flex;
  align-items: center;
  width: 200px;
}
.xcp-size-select .xcp__size-word {
  margin-right: 10px;
}
.xcp-size-select .xcp-size-select__btn {
  color: var(--gradient-base-background-1);
  font-weight: normal;
  text-transform: none;
  padding: 4px 20px;
  margin: 0 8px;
  height: 36px;
  display: inline-flex;
  align-items: center;
}
.xcp-size-select .xcp-size-select__btn:hover {
  text-decoration: none;
  color: var(--gradient-base-background-1);
}
.xcp-size-select .xcp-size-select__btn--4x2 {
  color: #eee;
  background-color: var(--gradient-base-background-1);
  border: 2px solid #eee;
  font-weight: normal;
  text-transform: none;
  padding: 4px 20px;
  margin: 0 8px;
}
.xcp-size-select .xcp-size-select__btn--4x2:hover {
  background-color: #ffb256;
  border: 2px solid #ffb256;
}
{% endstyle %}


{% comment %}
// Uncomment when the 4x2 is live and available for purchase
// UPDATE the href for the link to the 4x2

<div class="xcp-size-select">
  <span class="xcp__size-word">Size</span>
  <button class="btn btn-primary xcp-size-select__btn">4x4</button>
  {% comment %}
    link to 4x2 XCP product page
  {% endcomment %}
  <a class="btn btn-primary xcp-size-select__btn xcp-size-select__btn--4x2" href="#">4x2</a>
</div>
{% endcomment %}

{% style %}
.product-form {
  background: none;
}
.product-form .product-form__buttons {
  width: 100%;
  max-width: none;
}
.shopify-payment-button__button--unbranded {
  background-color: #FFB250;
  color: black;
}
.quantity {
  width: 30%;
  padding: 0 !important;
  margin: 0;
}
.quantity__input {
  text-align: center !important;
  padding: 0 5px 0 5px 0 !important;
  margin: 0;
  width: 2em;
  flex-grow: 0 !important;
}
.quantity__button {
  width: fit-content !important;
}
{% endstyle %}

<div id="clyde-cta"></div>

{% style %}
.product a {
  color: #eee;
  font-weight: bold;
}
.product a:hover {
  color: #ffb256;
  text-decoration: underline;
}
{% endstyle %}

For Business Financing options <a href="https://inventables.gogc.com/">Apply Here</a>

{% style %}
/* title */
.xcp-complete__title {
  text-align: center;
}
@media screen and (min-width: 750px) {
.xcp-complete__title {
  display: flex;
  justify-content: center;
}
}
.xcp-complete__title h2 {
  font-size: 3rem;
  font-weight: normal;
  text-transform: uppercase;
}
@media screen and (min-width: 750px) {
  .xcp-complete__title h2 {
    font-size: 3.5rem;
    font-weight: normal;
    text-transform: uppercase;
  }
}
.xcp-complete__title h2 em {
  color: #ffb256;
  font-weight: bold;
  font-style: normal;
}

/**** body */

.xcp-complete__hw-sw-support {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 0 auto;
  padding: 10px 0 40px 0;
}
@media screen and (min-width: 750px) {
.xcp-complete__hw-sw-support 
  {
    flex-direction: row;
    align-items: normal;
  }
}
.xcp-complete__hw-sw-support h3,
.xcp-complete__hw-sw-support li {
  text-transform: uppercase;
}
.xcp-complete__hw-sw-support h3 {
  font-size: 3rem;
  font-weight: bold;
  margin: 10px 0;
}
.xcp-complete__hw-sw-support h3.xcp-complete__plus {
  font-size: 4rem;
  display: block;
  position: absolute;
  bottom: -35px;
  right: 50%;
  z-index: 1;
}
@media screen and (min-width: 750px) {
  .xcp-complete__hw-sw-support h3.xcp-complete__plus {
    font-size: 6rem;
    top: 35%;
    right: -17px;
  }
}
.xcp-complete__section {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
}
@media screen and (min-width: 750px) {
.xcp-complete__section {
  padding: 20px;
  width: 375px;
}}

.xcp-complete__section img {
  width: 70vw;
  margin: 40px 0;
  background-color: #fff;
  border-radius: 10px;
  border: 3px solid #ffb256;
}

@media screen and (min-width: 750px) {
.xcp-complete__section img {
  height: 200px;
  width: auto;
  margin: 10px 0;
}}

.xcp-complete__section.xcp-complete__hw {
  background-color: rgba(223, 223, 223, 0.15);
}
.xcp-complete__section.xcp-complete__sw {
  background-color: rgba(77,77,77,0.35);
}
.xcp-complete__section.xcp-complete__support {
  background-color: #212223;
}
{% endstyle %}

<hr class="inv-hr" />

<div class="xcp-complete__title">
<h2>You will get a <em>complete</em> CNC solution</h2>
</div>

<div class="xcp-complete__hw-sw-support">
  <div class="xcp-complete__section xcp-complete__hw">
    <img src="https://cdn.shopify.com/s/files/1/0604/2982/8153/files/xcp_hardware.jpg?v=1684340948" />
    <h3>Hardware</h3>
    <ul>
      <li>X-Carve Pro 4x4 CNC Machine</li>
      <li>
        Dust Control System
        <ul>
          <li>Dust Collection Hose</li>
          <li>Dust Deputy Cyclone</li>
          <li>Fein Vacuum</li>
        </ul>
      </li>
      <li>Clamps To Hold Down Material</li>
      <li>Assembly Tools</li>
      <li>Bits For Planing, Cutting And 3d Carving</li>
    </ul>
    <h3 class="xcp-complete__plus">+</h3>
  </div>
  <div class="xcp-complete__section xcp-complete__sw">
    <img src="https://cdn.shopify.com/s/files/1/0604/2982/8153/files/xcp_software.png?v=1684340948" />
    <h3>Software</h3>
    <ul>
      <li>3 Years of Easel Pro CNC Software</li>
        <ul>
          <li>Includes 3D Carving</li>
        </ul>
      </li>
    </ul>
    <h3 class="xcp-complete__plus">+</h3>
  </div>
  <div class="xcp-complete__section xcp-complete__support">
    <img src="https://cdn.shopify.com/s/files/1/0604/2982/8153/files/xcp_support.jpg?v=1684340947" />
    <h3>Support</h3>
    <ul>
      <li>Lifetime Phone/Email Support</li>
      <li>1-Year Limited Warranty</li>
      <li>3-Year Extended Warranty (Available for $500)</li>
    </ul>
  </div>
</div>

<hr class="inv-hr" />

{% style %}
product-form.inventables-page-bottom-product-form {
  width: 300px;
  margin: 0 auto;
}
product-form.inventables-page-bottom-product-form button {
  text-transform: uppercase;
  font-weight: bold;
  color: #242728;
  background-color: #FFB256;
}
{% endstyle %}

<product-form class="product-form inventables-page-bottom-product-form">
                  <div class="product-form__error-message-wrapper" role="alert" hidden>
                    <svg
                      aria-hidden="true"
                      focusable="false"
                      role="presentation"
                      class="icon icon-error"
                      viewBox="0 0 13 13"
                    >
                      <circle cx="6.5" cy="6.50049" r="5.5" stroke="white" stroke-width="2"/>
                      <circle cx="6.5" cy="6.5" r="5.5" fill="#EB001B" stroke="#EB001B" stroke-width="0.7"/>
                      <path d="M5.87413 3.52832L5.97439 7.57216H7.02713L7.12739 3.52832H5.87413ZM6.50076 9.66091C6.88091 9.66091 7.18169 9.37267 7.18169 9.00504C7.18169 8.63742 6.88091 8.34917 6.50076 8.34917C6.12061 8.34917 5.81982 8.63742 5.81982 9.00504C5.81982 9.37267 6.12061 9.66091 6.50076 9.66091Z" fill="white"/>
                      <path d="M5.87413 3.17832H5.51535L5.52424 3.537L5.6245 7.58083L5.63296 7.92216H5.97439H7.02713H7.36856L7.37702 7.58083L7.47728 3.537L7.48617 3.17832H7.12739H5.87413ZM6.50076 10.0109C7.06121 10.0109 7.5317 9.57872 7.5317 9.00504C7.5317 8.43137 7.06121 7.99918 6.50076 7.99918C5.94031 7.99918 5.46982 8.43137 5.46982 9.00504C5.46982 9.57872 5.94031 10.0109 6.50076 10.0109Z" fill="white" stroke="#EB001B" stroke-width="0.7">
                    </svg>
                    <span class="product-form__error-message"></span>
                  </div>

                  {%- form 'product', product, id: product_form_id, class: 'form', novalidate: 'novalidate', data-type: 'add-to-cart-form' -%}
                    <input
                      type="hidden"
                      name="id"
                      value="{{ product.selected_or_first_available_variant.id }}"
                      disabled
                    >
                    <div class="product-form__buttons">
                      <button
                        type="submit"
                        name="add"
                        class="product-form__submit button button--full-width {% if block.settings.show_dynamic_checkout %}button--secondary{% else %}button--primary{% endif %}"
                        {% if product.selected_or_first_available_variant.available == false %}
                          disabled
                        {% endif %}
                      >
                        <span>
                          {%- if product.selected_or_first_available_variant.available -%}
                            {{ 'products.product.add_to_cart' | t }}
                          {%- else -%}
                            {{ 'products.product.sold_out' | t }}
                          {%- endif -%}
                        </span>
                        <div class="loading-overlay__spinner hidden">
                          <svg
                            aria-hidden="true"
                            focusable="false"
                            role="presentation"
                            class="spinner"
                            viewBox="0 0 66 66"
                            xmlns="http://www.w3.org/2000/svg"
                          >
                            <circle class="path" fill="none" stroke-width="6" cx="33" cy="33" r="30"></circle>
                          </svg>
                        </div>
                      </button>
                      {%- if block.settings.show_dynamic_checkout -%}
                        {{ form | payment_button }}
                      {%- endif -%}
                    </div>
                  {%- endform -%}
                </product-form>
